package jamulusaudio

import (
	"fmt"
	"testing"

	"github.com/stretchr/testify/assert"
)

func TestSilence(t *testing.T) {
	stream := NewSilentOpusStream()
	packet := stream.Next()
	assert.Equal(t, hexdump(packet[:]), "")
	packet = stream.Next()
	assert.Equal(t, hexdump(packet[:]), "")
}

// Generate a hex dump of the packet.
func hexdump(packet []byte) string {
	var hex string
	for _, b := range packet {
		hex = hex + fmt.Sprintf("%02x", b)
	}
	return hex
}
